function embaralhaLista () {
    lista_auxiliar = []
    for (let index = 0; index < 11; index++) {
        indice = Math.randomRange(0, list.length - 1)
        valor_sorteado = list.removeAt(indice)
        lista_auxiliar.push(valor_sorteado)
    }
    list = lista_auxiliar
}
