forever(function () {
    info.setScore(0)
    game.showLongText("Vamos estudar a Tabuada!", DialogLayout.Top)
    numeroOk = false
    while (!(numeroOk)) {
        multiplicador = game.askForNumber("Escolha o número", 2)
        if (convertToText(multiplicador).indexOf(".") < 0) {
            numeroOk = true
        } else {
            game.showLongText("O número não pode ter .", DialogLayout.Top)
        }
    }
    game.showLongText("Vamos começar! Com a tabuada do " + convertToText(multiplicador), DialogLayout.Top)
    embaralhaLista()
    for (let value of list) {
        resposta = game.askForNumber("" + multiplicador + "x" + value, 4)
        if (resposta == multiplicador * value) {
            info.changeScoreBy(10)
        } else {
            game.showLongText("Ops. A resposta certa é: " + convertToText(multiplicador * value), DialogLayout.Top)
        }
        pause(1000)
    }
    game.showLongText("Parabéns! Você conseguiu " + info.score() + " pontos!", DialogLayout.Top)
})
